x=ParaE(:,1); 
y=ParaE(:,3); 
z=ParaE(:,2); 
xlin = linspace(min(x), max(x), 100); 
ylin = linspace(min(y), max(y), 100);
[X,Y]=meshgrid(xlin, ylin);
% Z=griddata(x,y,z,X,Y,'natural');
% Z=griddata(x,y,z,X,Y,'cubic');
Z=griddata(x,y,z,X,Y,'v4'); 
mesh(X,Y,Z) 
axis tight; hold on 
plot3(x,y,z,'.','MarkerSize',15)
