function k=GetBestSingleExp(DcyDat, dpmin) 
   s=fitoptions(’Method’,’NonLinearLeastSquares’,… 
              ’Lower’, 0, ’Upper’, 0.3, ’Startpoint’, –0.002); 
   f=fittype(’exp((k*–1)*t)’,’options’,s, ’independent’, ’t’); 
   Time = (0:(length(DcyDat)–1))/dpmin; 
   cfun = fit(Time’,DcyDat,f); 
   k = cfun.k;

function [DestainTimeCourse, SynapticStrengthTimeCourse] = ...
DestainReleaseSite(NumberOfTetherUnits, Time_seconds, ...
                       NumberVesiclesPerUnit, prs_VsTime, ... 
                       alpha_VsTime, gamma_VsTime, ...
                       ActionPotentials_VsTime)
    DestainTimeCourse(length(Time_seconds)) = nan;
    SynapticStrengthTimeCourse(length(Time_seconds)) = 0;
    TetherUnits(1:NumberOfTetherUnits, 1:NumberVesiclesPerUnit) = 1;
         %All vesicles in all tether units are stained to start
         %Each space on tether unit can be:
         %                                    1 (full, stained)
         %                                   -1(full, unstained)
         %                                    0 (vacant)
    DeltaTime_seconds = Time_seconds(2)-Time_seconds(1);
    DockedUnit = 1;
    for i = 1:length(Time_seconds)
         DestainTimeCourse(i) = sum(TetherUnits(:)==1);
         %fluorescence is equivalent to
         %the number of vesicles that are stained
         if (TransitionP(gamma_VsTime(i), DeltaTime_seconds))
              [DockedUnit, TetherUnits] =
                    SwitchDockedUnit(DockedUnit, ...
                                      1:NumberOfTetherUnits, ...
                                      TetherUnits);
         end
         if (TetherUnits(DockedUnit,end)==0)
               if (TransitionP(alpha_VsTime(i),DeltaTime_seconds))
                    TetherUnits = AdvanceTetherUnit(DockedUnit, TetherUnits);
         end
    end
    if (ActionPotentials_VsTime(i) && ...
         (TetherUnits(DockedUnit,end)~=0) && ...
         (prs_VsTime(i)) > rand())
               TetherUnits(DockedUnit,end) = 0;
               SynapticStrengthTimeCourse(i) = 1;
    end
 end

function P = TransitionP(DeltaTime_seconds, rateconstant)
     P = ((1-exp(-rateconstant*DeltaTime_seconds)) > rand());

function [NewDocked, TetherUnits] = ...
             SwitchDockedUnit(CurrentlyDocked, AllChoices, TetherUnits)
     AllChoices(AllChoices==CurrentlyDocked) = [];
     NewDocked = randsample(AllChoices, 1);
     TetherUnits(NewDocked, TetherUnits(NewDocked, :)==0) = -1;
     % The preceding line replaces vacant spaces
     % on tethers with unstained vesicles

function TetherUnits = AdvanceTetherUnit(DockedUnit, TetherUnits)
    if (TetherUnits(DockedUnit,end) ~= 0) %Only advance if vacant
        warning(’Trying to advance a chain with no vacancy at end’);
    else
        for i = length(TetherUnits(DockedUnit,:)):-1:2
         TetherUnits(DockedUnit, i) = TetherUnits(DockedUnit,i-1);
        end
        TetherUnits(DockedUnit, 1) = 0; %This makes the end vacant
 end
