p.code:
## R function to compute TOST p-value based on effect estimate, standard
## error, null value (default is 0), equivalence margin from step 1.
pTOST <- function(estimate, se, null = 0, margin) {
 p1 <- pnorm(q = (estimate - null - margin)/se)
 p2 <- 1 - pnorm(q = (estimate - null + margin)/se)
 p <- pmax(p1, p2)
 return(p)
}

## R function to compute Bayes factor based on effect estimate, standard
## error, null value (default is 0), prior mean (default is null value), 
## and prior standard deviation from step 1.
BF01 <- function(estimate, se, null = 0, priormean = null, priorsd) {
 bf <- sqrt(1 + priorsd^2/se^2)*exp(-0.5*((estimate - null)^2/se^2 -
 (estimate - priormean)^2/(se^2 + priorsd^2)))
 return(bf)
}
